mapper.select:
SELECT AP.* FROM ( SELECT A.DEVICE_ID AS apId , A.SERIAL_NO AS serialNo , A.DEVICE_STUS_CD AS deviceStatusCd ,(SELECT MLANG_COM_CD_NAME FROM TB_SYS_MLANG_COM_CD WHERE COM_CD = A.DEVICE_STUS_CD AND LANG_CLS_CD = #{lang} ) AS deviceStatusName , B.LOCATE_CLS_CD , B.LOCATE_ID ,(if (B.LOCATE_CLS_CD = 'S', (SELECT S.STATION_GRP_SEQ FROM TB_OPR_STATION S WHERE S.STATION_ID = B.LOCATE_ID ),'' )) AS stationGuId ,(case when B.LOCATE_CLS_CD = 'C' then (SELECT MCOM.MLANG_COM_CD_NAME FROM TB_SYS_MLANG_COM_CD MCOM JOIN TB_OPR_CENTER OPCEN ON OPCEN.DISTRICT_CD = MCOM.COM_CD WHERE MCOM.LANG_CLS_CD = #{lang} AND OPCEN.CENTER_ID = B.LOCATE_ID)
else (SELECT OPMLST.STATION_GRP_NAME FROM TB_OPR_MLANG_STATION_GRP OPMLST JOIN TB_OPR_STATION OPST ON OPST.STATION_GRP_SEQ = OPMLST.STATION_GRP_SEQ WHERE OPMLST.LANG_CLS_CD = #{lang} AND OPST.STATION_ID = B.LOCATE_ID ) end) AS stationGuName ,(case when B.LOCATE_CLS_CD = 'C' then (SELECT CENTER_NAME FROM TB_OPR_CENTER WHERE CENTER_ID = B.LOCATE_ID) else (SELECT STATION_NAME FROM TB_OPR_MLANG_STATION WHERE STATION_ID = B.LOCATE_ID AND LANG_CLS_CD = #{lang}) end) AS stationName
FROM TB_IOT_DEVICE A INNER JOIN TB_IOT_AP_LOCATE_INFO B ON A.DEVICE_ID = B.DEVICE_ID WHERE B.LOCATE_END_DTTM = STR_TO_DATE('9999-12-31 23:59:59','%Y-%m-%d %H:%i:%S') ) AP WHERE 1=1
AND AP.apId LIKE CONCAT('%',#{apId}, '%')
AND AP.LOCATE_CLS_CD = #{searchType}
AND AP.deviceStatusCd = #{deviceStatusCd}
AND AP.stationGuId = #{stationGroupId}
AND AP.LOCATE_ID = #{stationGroupId}
AND AP.LOCATE_ID = #{stationId}

SELECT RS.RACK_ID AS rackId ,RS.RACK_STUS_CD AS deviceStatusCd ,RS.DEVICE_STATUS_NAME AS deviceStatusName ,RS.NOW_STATION_EQUIP_ORD AS stationEquipOrder ,RS.NOW_LOCATE_ID AS locateId ,CONCAT(RS.CENTER_NAME, RS.STATION_NAME) AS stationName FROM (SELECT A.RACK_ID ,A.RACK_STUS_CD ,(SELECT MLANG_COM_CD_NAME FROM TB_SYS_MLANG_COM_CD WHERE COM_CD = A.RACK_STUS_CD AND LANG_CLS_CD = #{lang} ) AS DEVICE_STATUS_NAME ,A.NOW_STATION_EQUIP_ORD ,A.NOW_LOCATE_ID ,A.NOW_LOCATE_CLS_CD ,IF(CEN.CENTER_NAME IS NULL, '', CONCAT('(C)', CEN.CENTER_NAME)) CENTER_NAME ,IF(ST.STATION_NAME IS NULL, '', CONCAT('(S)', ST.STATION_NAME)) STATION_NAME FROM TB_OPR_RACK A LEFT OUTER JOIN TB_OPR_CENTER CEN ON A.NOW_LOCATE_ID = CEN.CENTER_ID LEFT OUTER JOIN ( SELECT ST.STATION_ID, MST.STATION_NAME, ST.STATION_GRP_SEQ FROM TB_OPR_STATION ST, TB_OPR_MLANG_STATION MST WHERE ST.STATION_ID = MST.STATION_ID AND MST.LANG_CLS_CD = #{lang}) ST ON A.NOW_LOCATE_ID = ST.STATION_ID ) RS WHERE 1 = 1
AND RS.RACK_STUS_CD = #{deviceStatusCd}
AND RS.NOW_LOCATE_CLS_CD = #{searchType}
AND CONCAT(RS.CENTER_NAME, RS.STATION_NAME) LIKE CONCAT('%', #{stationName},'%')
AND RACK_ID LIKE CONCAT('%', #{rackId},'%')

SELECT A.BIKE_ID AS bikeId ,A.BIKE_NO AS bikeNo ,A.BIKE_STUS_CD AS deviceStatusCd ,(SELECT MLANG_COM_CD_NAME FROM spb.TB_SYS_MLANG_COM_CD WHERE COM_CD = A.BIKE_STUS_CD AND LANG_CLS_CD = #{lang} ) AS deviceStatusName FROM spb.TB_OPR_BIKE A LEFT OUTER JOIN spb.TB_OPR_BIKE_PARKING B ON A.BIKE_ID = B.BIKE_ID LEFT OUTER JOIN spb.TB_OPR_RACK C ON B.RACK_ID = C.RACK_ID
A.BIKE_STUS_CD NOT IN ('BKS_002','BKS_007') AND (A.TERMINAL_ID IS NULL OR A.TERMINAL_ID = '')
AND A.BIKE_NO LIKE CONCAT('%',#{bikeNo},'%')
A.BIKE_NO LIKE CONCAT('%',#{bikeNo},'%')
AND C.NOW_LOCATE_CLS_CD = #{searchType}
AND A.BIKE_STUS_CD = #{deviceStatusCd}

SELECT A.DEVICE_ID AS terminalId , A.SERIAL_NO AS serialNo , A.DEVICE_STUS_CD AS deviceStatusCd ,(SELECT MLANG_COM_CD_NAME FROM TB_SYS_MLANG_COM_CD WHERE COM_CD = A.DEVICE_STUS_CD AND LANG_CLS_CD = #{lang} ) AS deviceStatusName FROM TB_IOT_DEVICE A INNER JOIN TB_IOT_TERMINAL_LOCATE_INFO B ON A.DEVICE_ID = B.DEVICE_ID WHERE B.LOCATE_END_DTTM = STR_TO_DATE('9999-12-31 23:59:59','%Y-%m-%d %H:%i:%S')
AND A.DEVICE_ID LIKE CONCAT('%',#{terminalId},'%')
AND B.LOCATE_CLS_CD = #{searchType}
AND A.DEVICE_STUS_CD = 'DES_003'
AND A.DEVICE_STUS_CD = #{deviceStatusCd}

