mapper.select:
SELECT T.FAULT_SEQ AS faultSeq, T.EQUIPMENT_ID AS equipmentId, T.EQUIPMENT_CLS_CD AS equipmentClsCd, T.MOD_ID AS reportId, T.STATION_NAME as stationName, T.FAULT_CONTENT as faultContent FROM (
SELECT A.FAULT_SEQ, B.BIKE_NO AS EQUIPMENT_ID, A.EQUIPMENT_CLS_CD, A.MOD_ID, (CASE WHEN D.LOCATE_CLS_CD = 'C' THEN CONCAT('(C)', (SELECT CENTER_NAME FROM TB_OPR_CENTER WHERE CENTER_ID = D.LOCATE_ID)) ELSE CONCAT('(S)', (SELECT STATION_NAME FROM TB_OPR_MLANG_STATION WHERE STATION_ID = D.LOCATE_ID AND LANG_CLS_CD = #{lang})) END) AS STATION_NAME, A.FAULT_CONTENT FROM TB_MTC_FAULT_INFO A LEFT OUTER JOIN TB_OPR_BIKE B ON A.EQUIPMENT_ID = B.BIKE_ID LEFT OUTER JOIN TB_OPR_BIKE_PARKING C ON B.BIKE_ID = C.BIKE_ID LEFT OUTER JOIN TB_OPR_RACK_LOCATE_INFO D ON C.RACK_ID = D.RACK_ID AND D.LOCATE_END_DTTM = STR_TO_DATE('9999-12-31 23:59:59','%Y-%m-%d %H:%i:%s')
SELECT A.FAULT_SEQ, A.EQUIPMENT_ID, A.EQUIPMENT_CLS_CD, A.MOD_ID, (CASE WHEN B.LOCATE_CLS_CD = 'C' THEN CONCAT('(C)', (SELECT CENTER_NAME FROM TB_OPR_CENTER WHERE CENTER_ID = B.LOCATE_ID)) ELSE CONCAT('(S)', (SELECT STATION_NAME FROM TB_OPR_MLANG_STATION WHERE STATION_ID = B.LOCATE_ID AND LANG_CLS_CD = #{lang})) END) AS STATION_NAME, A.FAULT_CONTENT FROM TB_MTC_FAULT_INFO A LEFT OUTER JOIN TB_IOT_AP_LOCATE_INFO B ON A.EQUIPMENT_ID = B.DEVICE_ID AND B.LOCATE_END_DTTM = STR_TO_DATE('9999-12-31 23:59:59','%Y-%m-%d %H:%i:%s')
SELECT A.FAULT_SEQ, A.EQUIPMENT_ID, A.EQUIPMENT_CLS_CD, A.MOD_ID, CONCAT((CASE WHEN B.LOCATE_CLS_CD = 'C' THEN CONCAT('(C)', (SELECT CENTER_NAME FROM TB_OPR_CENTER WHERE CENTER_ID = B.LOCATE_ID)) ELSE CONCAT('(S)', (SELECT STATION_NAME FROM TB_OPR_MLANG_STATION WHERE STATION_ID = B.LOCATE_ID AND LANG_CLS_CD = #{lang})) END), '/', B.STATION_EQUIP_ORD) AS STATION_NAME, A.FAULT_CONTENT FROM TB_MTC_FAULT_INFO A LEFT OUTER JOIN TB_OPR_RACK_LOCATE_INFO B ON A.EQUIPMENT_ID = B.RACK_ID AND B.LOCATE_END_DTTM = STR_TO_DATE('9999-12-31 23:59:59','%Y-%m-%d %H:%i:%s')
) T WHERE FAULT_SEQ = #{faultSeq}

SELECT COUNT(*) AS cnt
  FROM TB_MTC_FAULT_INFO
 WHERE EQUIPMENT_CLS_CD = #{deviceType}
   AND EQUIPMENT_ID = #{equipmentId}
   AND READ_CLS_CD = 'S'

