SELECT TEAM_SEQ AS teamSeq , CENTER_ID AS centerId , TEAM_ID AS teamId , (SELECT LA.MLANG_COM_CD_NAME FROM TB_SYS_COM_CD CD, TB_SYS_MLANG_COM_CD LA WHERE CD.COM_CD = LA.COM_CD AND CD.COM_CD = CENTER_ID AND LA.LANG_CLS_CD = 'LAG_001') as centerNm , (SELECT LA.MLANG_COM_CD_NAME FROM TB_SYS_COM_CD CD, TB_SYS_MLANG_COM_CD LA WHERE CD.COM_CD = LA.COM_CD AND CD.COM_CD = TEAM_ID AND LA.LANG_CLS_CD = 'LAG_001') as teamNm FROM TB_SYS_TEAM_INFO WHERE 1=1
AND CENTER_ID = #{centerId}
AND TEAM_ID = #{teamId}
