mapper.select:
SELECT COUNT(NOTICE_SEQ) AS noticeSeq FROM TB_COM_NOTICE WHERE 1=1
AND DATE_FORMAT(POST_STR_DTTM,'%Y-%m-%d') >= #{searchStartDate}
AND DATE_FORMAT(POST_END_DTTM,'%Y-%m-%d') <= #{searchEndDate}
AND ( NOTICE_TITLE LIKE CONCAT('%',#{searchValue},'%') OR NOTICE_TEXT_CONTENT LIKE CONCAT('%',#{searchValue},'%') )
AND MAIN_NOTICE_YN = 'Y'
AND MAIN_NOTICE_YN = 'N'

SELECT A. NOTICE_SEQ AS noticeSeq, CATE_CD AS cateCD, SITE_CLS_CD AS siteClsCD, NOTICE_NO AS noticeNO, NOTICE_TITLE AS noticeTitle, SORT_ORD AS sortOrd, POST_STR_DTTM AS postStrDttm, POST_END_DTTM AS postEndDttm, MAIN_NOTICE_YN AS mainNoticeYN, REG_DTTM AS regDttm, REG_ID AS regID, MOD_DTTM AS modDttm, MOD_ID AS modID FROM ( SELECT NOTICE_SEQ, CATE_CD, SITE_CLS_CD, NOTICE_NO, NOTICE_TITLE, SORT_ORD, POST_STR_DTTM, POST_END_DTTM, MAIN_NOTICE_YN, REG_DTTM, REG_ID, MOD_DTTM, MOD_ID FROM TB_COM_NOTICE WHERE 1=1
AND DATE_FORMAT(POST_STR_DTTM,'%Y-%m-%d') >= #{searchStartDate}
AND DATE_FORMAT(POST_END_DTTM,'%Y-%m-%d') <= #{searchEndDate}
AND ( NOTICE_TITLE LIKE CONCAT('%',#{searchValue},'%') OR NOTICE_TEXT_CONTENT LIKE CONCAT('%',#{searchValue},'%') )
AND MAIN_NOTICE_YN = 'Y'
AND MAIN_NOTICE_YN = 'N'
) A ORDER BY SORT_ORD DESC, NOTICE_SEQ DESC LIMIT #{firstRecordIndex},#{recordCountPerPage}

SELECT	NOTICE_SEQ AS noticeSeq,
		NOTICE_TITLE AS noticeTitle
FROM	TB_COM_NOTICE
WHERE	USE_YN = 'Y' AND NOTICE_SEQ < #{noticeSeq}

			AND DATE_FORMAT(POST_STR_DTTM,'%Y-%m-%d') >= #{searchStartDate}


			AND DATE_FORMAT(POST_END_DTTM,'%Y-%m-%d') <= #{searchEndDate}


			AND ( NOTICE_TITLE LIKE CONCAT('%',#{searchValue},'%') OR NOTICE_TEXT_CONTENT LIKE CONCAT('%',#{searchValue},'%') )

ORDER BY NOTICE_SEQ DESC
LIMIT	1

SELECT	NOTICE_SEQ AS noticeSeq,
		NOTICE_TITLE AS noticeTitle
FROM	TB_COM_NOTICE
WHERE	USE_YN = 'Y' AND NOTICE_SEQ > #{noticeSeq}

			AND DATE_FORMAT(POST_STR_DTTM,'%Y-%m-%d') >= #{searchStartDate}


			AND DATE_FORMAT(POST_END_DTTM,'%Y-%m-%d') <= #{searchEndDate}


			AND ( NOTICE_TITLE LIKE CONCAT('%',#{searchValue},'%') OR NOTICE_TEXT_CONTENT LIKE CONCAT('%',#{searchValue},'%') )

ORDER BY NOTICE_SEQ ASC
LIMIT	1

mapper.update:
UPDATE TB_COM_EDITOR_IMG
   SET BBS_SEQ = 0
 WHERE BBS_SEQ = #{noticeSeq}

UPDATE
	TB_COM_EDITOR_IMG
SET
	BBS_SEQ = #{noticeSeq}
WHERE BBS_CLS_CD = #{currentMenu} 

		AND IMG_NAME IN
		#{imagelist}
		|| IMG_NAME IN
		#{filelist}


		AND IMG_NAME IN
		#{filelist}


		AND IMG_NAME IN
		#{imagelist}

mapper.select:
SELECT	T1.NOTICE_SEQ as noticeSeq, (SELECT	COUNT(TT1.NOTICE_SEQ) AS COUNT FROM	TB_COM_NOTICE TT1) AS noticeNO
FROM	TB_COM_NOTICE T1
WHERE	T1.USE_YN = 'Y' AND T1.POPUP_YN = #{popupYN}
	AND DATE_FORMAT(T1.POST_STR_DTTM,'%Y%m%d') <= #{searchStartDate}
	AND DATE_FORMAT(T1.POST_END_DTTM,'%Y%m%d') >= #{searchEndDate}
	AND ( T1.SITE_CLS_CD LIKE CONCAT('%',#{siteClsCD},'%'))
