mapper.select:
		 SELECT COUNT(IA.QNA_SEQ) AS qnaSeq
		   FROM TB_COM_QNA_INFO IA 
LEFT OUTER JOIN TB_COM_QNA_INFO_REPLY IB 
			 ON IA.QNA_SEQ=IB.QNA_SEQ
		  WHERE IA.OPEN_YN = 'Y'



					AND DATE_FORMAT(IA.REG_DTTM,'%Y-%m-%d') >= #{searchStartDate}



					AND DATE_FORMAT(IA.REG_DTTM,'%Y-%m-%d') <= #{searchEndDate}


					AND  (  IA.QESTN_TITLE LIKE CONCAT('%',#{searchValue},'%')
							   OR
							IA.REG_ID	  LIKE CONCAT('%',replace(#{searchValue},' ',''),'%')
						 )


					AND IA.QNA_PROCESS_STUS_CD = 'D'


					AND IA.QNA_PROCESS_STUS_CD IS NULL


					AND ( IA.ADMIN_ID = #{comCd}
						  OR
						  IA.CATE_CD  = #{comCd}
						)

SELECT B.USR_SEQ AS WRTER_ID, QNA_SEQ, QESTN_TITLE, READ_CNT,
CAST(AES_DECRYPT(UNHEX(EMAIL_ADDR),'BRC') as CHAR) AS EMAIL_ADDR, USR_REG_DTTM, USR_REG_ID, USR_MOD_DTTM, USR_MOD_ID, QNA_PROCESS_STUS_CD, OPEN_YN, USE_YN, SECRET_YN, REG_DTTM, REG_ID, MOD_DTTM, MOD_ID, ADMIN_ID, DEADLINE, DEADLINE_DATE
FROM ( SELECT IA.QNA_SEQ, IA.QESTN_TITLE, IA.READ_CNT, IA.EMAIL_ADDR, IA.REG_DTTM AS USR_REG_DTTM, IA.REG_ID AS USR_REG_ID, IA.MOD_DTTM AS USR_MOD_DTTM, IA.MOD_ID AS USR_MOD_ID, IA.QNA_PROCESS_STUS_CD, IA.OPEN_YN, IA.SECRET_YN, IB.USE_YN, IB.REG_DTTM, IB.REG_ID, IB.MOD_DTTM, IB.MOD_ID,
(SELECT MLANG_COM_CD_NAME FROM TB_SYS_MLANG_COM_CD ML WHERE ML.LANG_CLS_CD = 'LAG_001' AND ML.COM_CD = IA.ADMIN_ID ) AS ADMIN_ID, IA.DEADLINE, IA.DEADLINE_DATE FROM TB_COM_QNA_INFO IA LEFT OUTER JOIN TB_COM_QNA_INFO_REPLY IB ON IA.QNA_SEQ = IB.QNA_SEQ WHERE IA.OPEN_YN = 'Y'
AND DATE_FORMAT(IA.REG_DTTM,'%Y-%m-%d') >= #{searchStartDate}
AND DATE_FORMAT(IA.REG_DTTM,'%Y-%m-%d') <= #{searchEndDate}
AND ( IA.QESTN_TITLE LIKE CONCAT('%',#{searchValue},'%') OR IA.REG_ID LIKE CONCAT('%',replace(#{searchValue},' ',''),'%') )
AND IA.QNA_PROCESS_STUS_CD = 'D'
AND IA.QNA_PROCESS_STUS_CD IS NULL
AND ( IA.ADMIN_ID = #{comCd} OR IA.CATE_CD = #{comCd} )
) A , TB_SVC_MB B WHERE A.USR_REG_ID = B.MB_ID ORDER BY A.QNA_SEQ DESC LIMIT #{firstRecordIndex},#{recordCountPerPage}

SELECT IA.QNA_SEQ , IA.QESTN_TITLE , IB.REG_ID FROM TB_COM_QNA_INFO IA LEFT OUTER JOIN TB_COM_QNA_INFO_REPLY IB ON IA.QNA_SEQ = IB.QNA_SEQ WHERE IA.OPEN_YN = 'Y' AND IA.QNA_SEQ < #{qnaSeq}
AND DATE_FORMAT(IB.MOD_DTTM,'%Y-%m-%d') >= #{searchStartDate}
AND DATE_FORMAT(IB.MOD_DTTM,'%Y-%m-%d') <= #{searchEndDate}
AND IA.QESTN_TITLE LIKE CONCAT('%',#{searchValue},'%')
AND IA.QNA_PROCESS_STUS_CD = 'D'
AND IA.QNA_PROCESS_STUS_CD IS NULL
ORDER BY QNA_SEQ DESC LIMIT 1

SELECT IA.QNA_SEQ , IA.QESTN_TITLE , IB.REG_ID FROM TB_COM_QNA_INFO IA LEFT OUTER JOIN TB_COM_QNA_INFO_REPLY IB ON IA.QNA_SEQ = IB.QNA_SEQ WHERE IA.OPEN_YN = 'Y' AND IA.QNA_SEQ > #{qnaSeq}
AND DATE_FORMAT(IB.MOD_DTTM,'%Y-%m-%d') >= #{searchStartDate}
AND DATE_FORMAT(IB.MOD_DTTM,'%Y-%m-%d') <= #{searchEndDate}
AND IA.QESTN_TITLE LIKE CONCAT('%',#{searchValue},'%')
AND IA.QNA_PROCESS_STUS_CD = 'D'
AND IA.QNA_PROCESS_STUS_CD IS NULL
ORDER BY QNA_SEQ ASC LIMIT 1

mapper.update:
UPDATE TB_COM_QNA_INFO
   SET QNA_PROCESS_STUS_CD = #{qnaProcessStusCD}

 WHERE OPEN_YN = 'Y' AND QNA_SEQ = #{qnaSeq}

 UPDATE TB_COM_QNA_INFO_REPLY
	SET ANSWER_TEXT_CONTENT = #{answerTextContent},
		ANSWER_HTML_CONTENT = #{answerHTMLContent},
		MOD_DTTM = NOW(),
		MOD_ID = #{modID}
  WHERE USE_YN = 'Y' AND QNA_SEQ = #{qnaSeq}

UPDATE TB_COM_EDITOR_IMG
   SET BBS_SEQ = 0
 WHERE BBS_SEQ = #{qnaSeq}

UPDATE TB_COM_EDITOR_IMG
   SET BBS_SEQ = #{qnaSeq}
 WHERE BBS_CLS_CD = #{currentMenu} 
   AND IMG_NAME IN
#{imagelist}

UPDATE TB_COM_QNA_INFO
   SET OPEN_YN = #{openYN}
 WHERE QNA_SEQ = #{qnaSeq}

UPDATE TB_COM_QNA_INFO
   SET ADMIN_ID = #{adminId}
 WHERE OPEN_YN = 'Y' 
   AND QNA_SEQ = #{qnaSeq}

 UPDATE TB_COM_QNA_INFO
	SET DEADLINE_DATE = PROC_ADMIN.getAnswerDate(REG_DTTM, 14)
	  , DEADLINE = #{deadline}

  WHERE OPEN_YN = 'Y'
	AND QNA_SEQ = #{qnaSeq}
