mapper.select:
SELECT COUNT(*)
  FROM TB_IOT_DEVICE



	  SERIAL_NO = #{chkId}  


	  DEVICE_ID = #{chkId}

mapper.update:
UPDATE TB_OPR_QR_BEACON SET 
	NOW_LOCATE_ID= #{stationId}
	,NOW_LOCATE_CLS_CD = (case 
		   when (SELECT COUNT(*) FROM TB_OPR_CENTER WHERE CENTER_ID=#{stationId}) > 0 THEN 'C'
		   else 'S'
		end) 
	,MOD_DTTM= NOW()
WHERE BEACON_ID = #{deviceId}
