mapper.select:
SELECT A.PAYMENT_FAIL_HIST_SEQ AS paymentFailHistSeq , A.RENT_DTTM AS rentDttm , A.RENT_HIST_SEQ AS rentHistSeq , A.PAYMENT_DTTM AS paymentDttm , A.PAYMENT_METHOD_CD AS paymentMethodCd ,(SELECT MLANG_COM_CD_NAME FROM TB_SYS_MLANG_COM_CD WHERE COM_CD = A.PAYMENT_METHOD_CD AND LANG_CLS_CD = #{lang}) AS paymentMethodCdName , A.PAYMENT_CLS_CD AS paymentClsCd , (SELECT MLANG_COM_CD_NAME FROM TB_SYS_MLANG_COM_CD WHERE COM_CD = A.PAYMENT_CLS_CD AND LANG_CLS_CD = #{lang}) AS paymentClsCdName , A.PAYMENT_AMT AS paymentAmt , A.ERR_CD AS errCd , A.ERR_MSG AS errMsg FROM TB_SVC_PAYMENT_FAIL_HIST A WHERE 1=1
AND A.PAYMENT_DTTM BETWEEN STR_TO_DATE(#{startPaymentDttm}, '%Y-%m-%d') AND (STR_TO_DATE(#{endPaymentDttm}, '%Y-%m-%d') + INTERVAL 1 DAY - INTERVAL 1 SECOND)
AND A.PAYMENT_DTTM BETWEEN DATE_FORMAT(#{startPaymentDttm}, '%Y-%m-%d') AND (DATE_FORMAT(now(), '%Y-%m-%d') + INTERVAL 1 DAY - INTERVAL 1 SECOND)
AND A.PAYMENT_DTTM BETWEEN DATE_FORMAT(now(), '%Y-%m-%d') AND (DATE_FORMAT(now(), '%Y-%m-%d') + INTERVAL 1 DAY - INTERVAL 1 SECOND)
AND A.PAYMENT_DTTM BETWEEN DATE_FORMAT(now(), '%Y-%m-%d') AND (DATE_FORMAT(now(), '%Y-%m-%d') + INTERVAL 1 DAY - INTERVAL 1 SECOND)
AND A.PAYMENT_CLS_CD = #{paymentClsCd}
AND A.PAYMENT_METHOD_CD = #{paymentMethodCd}
AND A.RENT_HIST_SEQ = #{rentHistSeq}
ORDER BY A.PAYMENT_DTTM DESC
LIMIT #{firstRecordIndex}, #{recordCountPerPage}


SELECT COUNT(*)
FROM TB_SVC_PAYMENT_FAIL_HIST A
WHERE 1=1


AND A.PAYMENT_DTTM  BETWEEN STR_TO_DATE(#{startPaymentDttm}, '%Y-%m-%d') AND (STR_TO_DATE(#{endPaymentDttm}, '%Y-%m-%d') + INTERVAL 1 DAY - INTERVAL 1 SECOND)


AND A.PAYMENT_DTTM BETWEEN DATE_FORMAT(now(), '%Y-%m-%d') AND (DATE_FORMAT(now(), '%Y-%m-%d') + INTERVAL 1 DAY - INTERVAL 1 SECOND)


AND A.PAYMENT_CLS_CD = #{paymentClsCd}


AND A.PAYMENT_METHOD_CD = #{paymentMethodCd}


AND A.RENT_HIST_SEQ = #{rentHistSeq}

mapper.insert:
		INSERT INTO TB_SVC_PAYMENT_FAIL_HIST (
                 USR_SEQ
                ,PAYMENT_DTTM
                ,PAYMENT_METHOD_CD
                ,PAYMENT_CLS_CD
                ,PAYMENT_AMT
                ,RENT_HIST_SEQ
                ,ERR_CD
                ,ERR_MSG
        ) VALUES (
                #{usrSeq}
               ,now()
               ,#{paymentMethodCd}
               ,'BIL_009'
               ,#{totAmt}
               ,(SELECT MAX(RENT_HIST_SEQ) FROM TB_SVC_RENT_HIST WHERE USR_SEQ = #{usrSeq})
               ,#{errCd}
               ,#{errMsg}
        )
