mapper.update:
UPDATE TB_SYS_MLANG_COM_CD
   SET MLANG_COM_CD_NAME = #{comCdName}
 WHERE COM_CD = #{comCd}
   AND LANG_CLS_CD = #{lang}

UPDATE TB_SYS_COM_CD
   SET CD_DESC1 = #{cdDesc1}
	  ,ADD_VAL1 = #{addVal1}
	  ,ADD_VAL2 = #{addVal2}
	  ,ADD_VAL3 = #{addVal3}
	  ,DSP_ORD  = #{dspOrd}
	  ,USE_YN   = #{useYn} 
 WHERE COM_CD = #{comCd}

mapper.select:
SELECT A.COM_CD AS comCd ,A.ADD_VAL1 AS addVal1 ,A.ADD_VAL2 AS addVal2 ,A.ADD_VAL3 AS addVal3 ,B.MLANG_COM_CD_NAME AS comCdName FROM TB_SYS_COM_CD A INNER JOIN TB_SYS_MLANG_COM_CD B ON A.COM_CD = B.COM_CD WHERE USE_YN = 'Y'
AND A.COM_CD = #{comCd}
AND B.LANG_CLS_CD = #{lang} ORDER BY CAST(A.DSP_ORD AS UNSIGNED)

SELECT A.COM_CD AS comCd ,A.COM_UP_CD AS comUpCd ,A.DSP_ORD AS dspOrd ,A.USE_YN AS useYn ,A.CD_DESC1 AS cdDesc1 ,A.CD_DESC2 AS cdDesc2 ,A.ADD_VAL1 AS addVal1 ,A.ADD_VAL2 AS addVal2 ,A.ADD_VAL3 AS addVal3 ,DATE_FORMAT(A.REG_DTTM, '%Y-%m-%d') AS regDttm ,B.MLANG_COM_CD_NAME AS comCdName ,A.REG_ID AS regId FROM TB_SYS_COM_CD A LEFT OUTER JOIN TB_SYS_MLANG_COM_CD B ON A.COM_CD = B.COM_CD
B.LANG_CLS_CD = #{lang}
AND A.COM_UP_CD = #{comUpCd}
AND A.COM_UP_CD IS NULL
AND B.MLANG_COM_CD_NAME LIKE CONCAT(#{searchWord}, '%')
AND STR_TO_DATE(#{searchBgnDe}, '%Y-%m-%d') <= A.REG_DTTM
AND A.REG_DTTM <= (STR_TO_DATE(#{searchEndDe}, '%Y-%m-%d') + INTERVAL 1 DAY - INTERVAL 1 SECOND)
ORDER BY A.DSP_ORD
ORDER BY A.REG_DTTM DESC
LIMIT #{firstRecordIndex}, #{recordCountPerPage}

SELECT COUNT(*) FROM TB_SYS_COM_CD A LEFT OUTER JOIN TB_SYS_MLANG_COM_CD B ON A.COM_CD = B.COM_CD WHERE B.LANG_CLS_CD = #{lang}
AND A.COM_UP_CD = #{comUpCd}
AND A.COM_UP_CD IS NULL
AND B.MLANG_COM_CD_NAME LIKE CONCAT(#{searchWord}, '%')
AND A.REG_DTTM BETWEEN STR_TO_DATE(#{searchBgnDe}, '%Y-%m-%d') AND (STR_TO_DATE(#{searchEndDe}, '%Y-%m-%d') + INTERVAL 1 DAY - INTERVAL 1 SECOND)

SELECT A.COM_CD AS comCd ,A.COM_UP_CD AS comUpCd ,A.CD_DESC1 AS cdDesc1 ,A.CD_DESC2 AS cdDesc2 ,DATE_FORMAT(A.REG_DTTM, '%Y-%m-%d') AS regDttm ,A.USE_YN AS useYn ,A.DSP_ORD AS dspOrd ,B.MLANG_COM_CD_NAME AS comCdName FROM FROM TB_SYS_COM_CD A INNER JOIN TB_SYS_MLANG_COM_CD B ON A.COM_CD = B.COM_CD WHERE B.LANG_CLS_CD = #{lang} AND A.USE_YN = 'Y'
AND COM_UP_CD = #{comUpCd}
AND USE_YN = #{useYn}
ORDER BY DSP_ORD

SELECT COUNT(*)
  FROM TB_SYS_MLANG_COM_CD
 WHERE COM_CD = #{comCd}
   AND LANG_CLS_CD = #{lang}
