mapper.select:
SELECT USR.ADMIN_ID AS usrId , USR.ADMIN_NAME AS usrName , USR.ADMIN_GRP_SEQ AS usrGrpCd , GRP.GRP_NAME AS grpName , CAST(AES_DECRYPT(UNHEX(USR.ADMIN_TEL_NO),'BRC') as CHAR) AS phoneNo , CAST(AES_DECRYPT(UNHEX(USR.ADMIN_MPN_NO),'BRC') as CHAR) AS mpnNo , CAST(AES_DECRYPT(UNHEX(USR.ADMIN_EMAIL_NAME),'BRC') as CHAR) AS email , USR.DEPT_NAME deptName , DATE_FORMAT(USR.CRE_DTTM, '%Y-%m-%d') as regDate , DATE_FORMAT(USR.MOD_DTTM, '%Y-%m-%d') as modDate , USR.USE_YN AS useYn FROM TB_SYS_ADMIN AS USR INNER JOIN TB_SYS_GRP_CD AS GRP ON USR.ADMIN_GRP_SEQ = GRP.ADMIN_GRP_SEQ WHERE 1=1
AND USR.ADMIN_NAME LIKE CONCAT(#{searchUsrName}, '%')
AND USR.ADMIN_ID LIKE CONCAT(#{searchUsrName}, '%')
AND USR.ADMIN_GRP_SEQ = #{searchUsrGrpCd}
AND USR.ACCS_CTRL_YN = #{accs_ctrl_yn}
ORDER BY USR.ACCS_CTRL_YN, USR.MOD_DTTM DESC LIMIT #{firstRecordIndex}, #{recordCountPerPage}

SELECT CAST(AES_DECRYPT(UNHEX(USR.ADMIN_MPN_NO),'BRC') as CHAR) AS mpn_no FROM TB_SYS_ADMIN AS USR WHERE 1=1
AND USR.ADMIN_NAME LIKE CONCAT(#{searchUsrName}, '%')
AND USR.ADMIN_ID LIKE CONCAT(#{searchUsrName}, '%')
AND USR.ADMIN_GRP_SEQ = #{searchUsrGrpCd}
AND USR.ACCS_CTRL_YN = #{accs_ctrl_yn}

SELECT COUNT(USR.ADMIN_ID) FROM TB_SYS_ADMIN AS USR INNER JOIN TB_SYS_GRP_CD AS GRP ON USR.ADMIN_GRP_SEQ = GRP.ADMIN_GRP_SEQ WHERE 1=1
AND USR.ADMIN_NAME LIKE CONCAT(#{searchUsrName}, '%')
AND USR.ADMIN_ID LIKE CONCAT(#{searchUsrName}, '%')
AND USR.ADMIN_GRP_SEQ = #{searchUsrGrpCd}
AND USR.ACCS_CTRL_YN = #{accs_ctrl_yn}

mapper.insert:
INSERT INTO TB_SYS_ADMIN ( ADMIN_ID , ADMIN_GRP_SEQ , ENC_PWD , ADMIN_NAME
, ADMIN_TEL_NO
, ADMIN_MPN_NO , USE_YN , ADMIN_EMAIL_NAME , EMAIL_RECV_YN
, ASSIGN_STATION_ID
, ASSIGN_CENTER_ID
, ACCS_CTRL_YN , ACCS_CTRL_PUBL_IP
, ADMIN_CARD_NO
, ADMIN_CARD_PWD
, RELOCATE_CAR_ID
, CRE_DTTM , MOD_DTTM , CRE_ID , MOD_ID , TEAM_SEQ ) VALUES( #{usrId} , #{usrGrpCd}
, SHA2(#{encPwd}, 512) , #{usrName}
, HEX(AES_ENCRYPT(#{phoneNo},'BRC'))
, HEX(AES_ENCRYPT(#{mpnNo},'BRC')) , #{useYn}
, HEX(AES_ENCRYPT(#{email},'BRC')) , #{email_recv_yn}
, CONCAT(#{assign_station_id}, '/', #{admin_grp_seq})
, #{assign_center_id}
, #{accs_ctrl_yn} , #{accs_ctrl_publ_ip}
, HEX(AES_ENCRYPT(#{admin_card_no},'BRC'))
, HEX(AES_ENCRYPT(#{adminCardPwd},'BRC'))
, #{relocateCarCd}
, SYSDATE() , SYSDATE() , #{cre_Id} , #{cre_Id} , '728' )

mapper.update:

	UPDATE 
		TB_SYS_ADMIN
	SET
		ADMIN_GRP_SEQ = #{usrGrpCd}
		, MOD_DTTM = SYSDATE()



		, ENC_PWD = CASE WHEN ENC_PWD = HEX(AES_ENCRYPT(#{encPwd},'BRC')) THEN ENC_PWD


					ELSE SHA2(#{encPwd}, 512)
					END
		, LOGIN_FAIL_CNT = 0



		,ADMIN_EMAIL_NAME = HEX(AES_ENCRYPT(#{email},'BRC'))



		,ADMIN_TEL_NO = HEX(AES_ENCRYPT(#{phoneNo},'BRC'))


		,ADMIN_NAME = #{usrName}



		,ADMIN_MPN_NO = HEX(AES_ENCRYPT(#{mpnNo},'BRC'))


		,USE_YN = #{useYn}


		,EMAIL_RECV_YN = #{email_recv_yn}



		,ADMIN_CARD_NO = HEX(AES_ENCRYPT(#{admin_card_no},'BRC'))


		,ASSIGN_STATION_ID = CONCAT(#{assign_station_id}, '/',  #{admin_grp_seq})


		,ASSIGN_CENTER_ID = #{assign_center_id}


		,ACCS_CTRL_YN = #{accs_ctrl_yn}


		,ACCS_CTRL_PUBL_IP = #{accs_ctrl_publ_ip}



		,ADMIN_CARD_PWD = HEX(AES_ENCRYPT(#{adminCardPwd},'BRC'))


		, RELOCATE_CAR_ID = #{relocateCarCd}


		, TEAM_SEQ = #{team_id}

		, MOD_ID = #{mod_Id}
		, MOD_DTTM = SYSDATE()
WHERE
	ADMIN_ID = #{usrId}

mapper.insert:
INSERT INTO
	TB_SYS_ADMIN_USE_HIST
( 
	ADMIN_ID
	,MOD_DTTM
	, MOD_ID
	, USE_YN

)
VALUES(
	 #{usrId}
	 , SYSDATE()
	 , #{mod_Id}
	, #{useYn}

)

mapper.select:
SELECT A.TEAM_SEQ as teamSeq , A.TEAM_ID as teamId , (SELECT LA.MLANG_COM_CD_NAME FROM TB_SYS_COM_CD CD, TB_SYS_MLANG_COM_CD LA WHERE CD.COM_CD = LA.COM_CD AND CD.COM_CD = A.TEAM_ID AND LA.LANG_CLS_CD = 'LAG_001') as teamNm FROM TB_SYS_TEAM_INFO A WHERE 1=1
AND CENTER_ID = #{centerId}
ORDER BY TEAM_ID, CENTER_ID
